<?phpclass M_Data {//// Список всех статей//public static function articles_all()    {        $db = M_Mysql::GetInstance();        return $articles = $db->Select        ("SELECT * FROM " . TABLE . " ORDER BY id_article DESC");    }    //// Конкретная статья// public static function articles_get($id_article)    {        $db = M_Mysql::GetInstance();        return $article = $db->Select       ("SELECT * FROM " . TABLE . " WHERE  id_article ='" . $id_article . "'");    }//// Добавить статью//public  static function articles_new($title, $content)    {        $db = M_Mysql::GetInstance();                // Подготовка.        $title = trim($title);        $content = trim($content);        // Проверка.        if ($title == '') {            $errors['error'] = 'Title is not specified';            return false;        } else {           return $id = $db -> Insert(TABLE,['title'=>$title, 'content'=>$content]);        }    }    //// Изменить статью//public static function articles_edit($id_article, $title, $content, $views)    {        $db = M_Mysql::GetInstance();        return $aff = $db -> Update(TABLE,['title'=>$title, 'content'=>$content, 'views'=>$views],            "id_article ='" . $id_article . "'");    }//// Удалить статью//public  static function  articles_delete($id_article)    {        $db = M_Mysql::GetInstance();        return $aff = $db -> Delete(TABLE,"id_article ='" . $id_article . "'");    }//// Intro//public static function articles_intro($str, $len)    {        if (strlen($str['content']) > $len) {            $del = ['.',  '!', '?'];            for ($i = $len; $i < strlen($str['content']); $i++) {                if (in_array($str['content'][$i], $del) && $i >= $len) {                    $index = $i;                    break;                }            }            return substr($str['content'], 0, $index + 1);        } else return $str['content'];    } //// Comment//    public static function articles_comment($id_article, $comment)      {                $db = M_Mysql::GetInstance();        return $aff = $db -> Update(TABLE,['comment'=>$comment],            "id_article ='" . $id_article . "'");    }  // extraction of parameters function    public static function extract_params()    {        $get = explode('/', $_GET['q']);        $params['c'] = $get[0];        $params['a'] = $get[1];        if ( $get[1] == 'Show_all' && isset($get[2]) ){            $params['access'] = $get[2];        } else $params['access'] = '';        if ( $get[1] == 'Look' || $get[1] == 'Edit' || $get[1] == 'Delete' ||            $get[1] == 'Comment' && isset($get[2]) ) {            $params['id'] = $get[2];        }        if (  $get[1] == 'Comment' && isset($get[3])){            $params['comments'] = $get[3];        }        return $params;    }}